export default {
  data() {
    return {
      file: null,
      detectedImage: null
    };
  },
  methods: {
    handleFileChange(event) {
      this.file = event.target.files[0];
    },
    async uploadFile() {
      if (!this.file) {
        alert("Please select a file to upload.");
        return;
      }
      const reader = new FileReader();
      reader.onload = async () => {
        const base64String = reader.result.split(',')[1]; // Extract base64 string

        try {
          const response = await fetch("http://127.0.0.1:5000/process_image", {
            method: "POST",
            headers: {
              "Content-Type": "application/json"
            },
            body: JSON.stringify({
              image: base64String
            }) // Send base64 string in JSON format
          });
          if (response.ok) {
            alert("File uploaded successfully!");
          } else {
            alert("Failed to upload file.");
          }
        } catch (error) {
          console.error("Error uploading file:", error);
          alert("An error occurred while uploading the file.");
        }
      };
      reader.readAsDataURL(this.file); // Read the file as Data URL
    },
    async detectFacialLandmarks() {
      try {
        const response = await fetch("http://127.0.0.1:5000/get_processed_image", {
          method: "GET"
        });
        if (response.ok) {
          const base64String = await response.text(); // Get the Base64 string of the image

          // Convert Base64 string to image format
          const img = new Image();
          img.src = "data:image/jpeg;base64," + base64String;

          // Display the image
          this.detectedImage = img.src;
          alert("Facial landmarks detected successfully!");
        } else {
          alert("Failed to detect facial landmarks.");
        }
      } catch (error) {
        console.error("Error detecting facial landmarks:", error);
        alert("An error occurred while detecting facial landmarks.");
      }
    }
  }
};
