export default {
  data() {
    return {
      file: null,
      detectedImage: null
    };
  },
  methods: {
    handleFileChange(event) {
      this.file = event.target.files[0];
    },
    async uploadFile() {
      if (!this.file) {
        alert("Please select a file to upload.");
        return;
      }
      const formData = new FormData();
      formData.append('image', this.file);
      try {
        const response = await fetch("http://localhost:5000/process_image", {
          method: "POST",
          body: formData
        });
        if (response.ok) {
          alert("File uploaded successfully!");
        } else {
          alert("Failed to upload file.");
        }
      } catch (error) {
        console.error("Error uploading file:", error);
        alert("An error occurred while uploading the file.");
      }
    },
    async detectFacialLandmarks() {
      try {
        const response = await fetch("http://localhost:5000/get_processed_image", {
          method: "GET"
        });
        if (response.ok) {
          const imageBlob = await response.blob();
          const imageUrl = URL.createObjectURL(imageBlob);
          this.detectedImage = imageUrl;
          alert("Facial landmarks detected successfully!");
        } else {
          alert("Failed to detect facial landmarks.");
        }
      } catch (error) {
        console.error("Error detecting facial landmarks:", error);
        alert("An error occurred while detecting facial landmarks.");
      }
    }
  }
};
